function initVendors(){if(window.Swiper)new window.Swiper(".swiper-container",{spaceBetween:30,centeredSlides:!0,effect:"fade",autoplay:{delay:2500,disableOnInteraction:!1},pagination:{el:".swiper-pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}})}function initMobileMenu(){const e=document.querySelector(".open-mobile-menu"),n=document.querySelector(".close-mobile-menu"),t=document.querySelector(".mobile-menu");e.onclick=function(){t.style.display="flex",e.style.display="none",n.style.display="block"},n.onclick=function(){t.style.display="none",e.style.display="block",n.style.display="none"}}function setHeaderBehavior(){window.addEventListener("scroll",function(){const e=document.documentElement&&document.documentElement.scrollTop||window.pageYOffset||window.scrollY||0,n=document.querySelector("header");e>50&&!n.classList.contains("header--collapsed")?n.classList.add("header--collapsed"):e<=50&&n.classList.contains("header--collapsed")&&n.classList.remove("header--collapsed")})}function setCurrentFooterYear(){document.querySelector("footer time").textContent=`${(new Date).getFullYear()}`}window.onload=function(){setHeaderBehavior(),setCurrentFooterYear(),initMobileMenu(),initVendors()};